package variable_basic;

public class NumericandNondecimal {

	public static void main(String[] args)
	
	{
		// TODO Auto-generated method stub
		
		
		//Variable declaration
		
		Byte Rollno;  
		
		Short Salary;   //variable Declaration
		
		int Package;  // variable Declaration
		
		long Income;  //variable Declaration	
		
		
		//Variable initialization
		
		Rollno = 22;  
		
		Salary = 12500;  //variable Initialization
		
		Package = 170000;   //Variable Initialization
		
        Income = 500000;  //Variable initialization
		
        //variable usage
        
        
		System.out.println("My rollno is: "+Rollno);   
		
		System.out.println("My basic salary is: "+Salary);  //variable usage
		
		System.out.println("My annual packege is: "+Package); // variable usage
		
		System.out.println("My overall income is: "+Income); // variable usage
		
			
			
	}

}
